import { createStore } from 'vuex'
import api from './services/api'

export default createStore({
  state: {
    uploadStatus: '',   // uploading / success / failed
    ocrResults: {
      text: '',
      pages: [],
      image: ''
    },
    error: null
  },
  mutations: {
    setUploadStatus(state, status) {
      state.uploadStatus = status
    },
    setOcrResults(state, results) {
      state.ocrResults = results
    },
    setResults(state, { file, latex }) {   // üëà Êñ∞Â¢û
    state.ocrResults = { file, latex };
    },
    setError(state, error) {
      state.error = error
    },
    resetState(state) {
      state.uploadStatus = ''
      state.ocrResults = { text: '', pages: [], image: '' }
      state.error = null
    }
  },
  actions: {
    async uploadFile({ commit }, file) {
      commit('resetState')
      commit('setUploadStatus', 'uploading')

      try {
        const response = await api.uploadFile(file)

        let result = {}
        const rawData = response?.data

        // üîé ÊâìÂç∞ÂéüÂßãËøîÂõûÔºåÊñπ‰æøÊéíÊü•
        console.log("ÂêéÁ´ØÂéüÂßãËøîÂõû:", rawData)

        if (typeof rawData === 'object') {
          result = rawData
        } else if (typeof rawData === 'string') {
          try {
            result = JSON.parse(rawData)
          } catch {
            result = { text: rawData }
          }
        } else {
          result = { text: String(rawData) }
        }

        // ‚úÖ Ê†áÂáÜÂåñÔºöÁªü‰∏ÄÂ≠óÊÆµÔºåÈÅøÂÖçÂâçÁ´ØÂ¥©Ê∫É
        const normalized = {
          text: result.text || result.content || '',
          pages: Array.isArray(result.pages) ? result.pages : [],
          image: result.image || result.image_bytes || ''
        }

        console.log("Ê†áÂáÜÂåñÂêéÁöÑÁªìÊûú:", normalized)

        commit('setOcrResults', normalized)
        commit('setUploadStatus', 'success')
      } catch (error) {
        console.error('Upload failed:', error)

        let errMsg = 'An error occurred!'
        if (error.response?.data) {
          if (typeof error.response.data === 'string') {
            errMsg = error.response.data
          } else if (error.response.data.message) {
            errMsg = error.response.data.message
          }
        } else if (error.message) {
          errMsg = error.message
        }

        commit('setError', errMsg)
        commit('setUploadStatus', 'failed')
      }
    }
  }
})
