import { ref, onMounted } from "vue";
import * as pdfjsLib from "pdfjs-dist";
import pdfjsWorker from "pdfjs-dist/build/pdf.worker?url"; // ✅ 关键：通过 Vite/webpack 引入 worker

// 设置 pdfjs worker

export default {
  __name: 'Results',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    pdfjsLib.GlobalWorkerOptions.workerSrc = pdfjsWorker;
    const pdfUrl = ref("/sample.pdf"); // 你的 PDF 文件路径（可以替换成 API 返回的 URL）
    const canvasRef = ref(null);
    onMounted(async () => {
      console.log("加载的 PDF 路径：", pdfUrl.value);
      const loadingTask = pdfjsLib.getDocument(pdfUrl.value);
      const pdf = await loadingTask.promise;
      console.log("PDF 加载成功, 页数:", pdf.numPages);
      const page = await pdf.getPage(1);
      const viewport = page.getViewport({
        scale: 1.5
      });
      const canvas = canvasRef.value;
      const context = canvas.getContext("2d");
      canvas.height = viewport.height;
      canvas.width = viewport.width;
      const renderContext = {
        canvasContext: context,
        viewport: viewport
      };
      await page.render(renderContext).promise;
      console.log("PDF 页面渲染完成 ✅");
    });
    const __returned__ = {
      pdfUrl,
      canvasRef,
      ref,
      onMounted,
      get pdfjsLib() {
        return pdfjsLib;
      },
      get pdfjsWorker() {
        return pdfjsWorker;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
